import "./css/pages.css";
import "./css/tiles.css";
import info from "./images/three_dots_icon.png";
import React, { useState } from "react";

const UserTile = ({ user }) => {
  const [showMenu, setShowMenu] = useState(false);

  // Handler to toggle the view
  const handleMoreInfoClick = () => {
    setShowMenu(true); // Show menu when button is clicked
  };

  const handleBackClick = () => {
    setShowMenu(false); // Show user details when back button is clicked
  };

  return (
    <div className="user-tile">
      {showMenu ? (
        // Conditionally render the menu when showMenu is true
        <div className="menu">
          <h3>Menu</h3>
          <ul>
            <li>Option 1</li>
            <li>Option 2</li>
            <li>Option 3</li>
          </ul>
          <button onClick={handleBackClick}>Back</button>
        </div>
      ) : (
        // Conditionally render the user details when showMenu is false
        <>
          <button className="more-info" onClick={handleMoreInfoClick}>
            <img className="more-info-pic" src={info} alt="More Info" />
          </button>
          <div className="user-tile__header">
            <h4>
              Username<br />
              <div className="field-values">{user.name}</div>
            </h4>
            <h4>
              User ID<br />
              <div className="field-values">{user.id}</div>
            </h4>
          </div>
          <div className="user-tile__body">
            <p className="user-tile-boy-p">
              Email<br />
              <div className="field-values">{user.email}</div>
            </p>
            <p className="user-tile-boy-p">
              Status:<br />
              <div className="field-values">{user.status}</div>
            </p>
            <p className="user-tile-boy-p">
              Role<br />
              <div className="field-values"></div>
            </p>
            <p className="user-tile-boy-p">
              Currency<br />
              <div className="field-values">$$$$</div>
            </p>
            <p className="user-tile-boy-p">
              Pending Commission<br />
              <div className="field-values">$$$$</div>
            </p>
            <p className="user-tile-boy-p">
              Available Commission<br />
              <div className="field-values">$$$$$</div>
            </p>
          </div>
        </>
      )}
    </div>
  );
};

export default UserTile;
