// UsersTable.js
import React, { useMemo } from 'react';
import { useTable } from 'react-table';
import UserTile from './UserTile';
import './UsersTable.css'; // Optional: for custom styling

const ResultsTable = ({ data }) => {
  const columns = useMemo(
    () => [
      {
        Header: 'Users',
        accessor: 'user', // The key should be 'user' to access each user object
        Cell: ({ cell: { value } }) => <UserTile user={value} />, // Render UserTile in each cell
      },
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable(
    {
      columns,
      data,
    }
  );

  return (
    <table {...getTableProps()} className="users-table">
      <thead>
        {headerGroups.map(headerGroup => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map(column => (
              <th {...column.getHeaderProps()}>{column.render('Header')}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map(row => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map(cell => (
                <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
              ))}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

export default ResultsTable;
